mapper.select:
        SELECT
        

--         级联表的字段

        FROM yimiao  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.yimiao_name like CONCAT('%',#{params.yimiaoName},'%')
            
            
                and a.yimiao_types = #{params.yimiaoTypes}
            
            
                  and a.yimiao_kucun_number >= #{params.yimiaoKucunNumberStart}   
            
            
                  and a.yimiao_kucun_number <= #{params.yimiaoKucunNumberEnd}   
            
             
                and a.yimiao_kucun_number = #{params.yimiaoKucunNumber}
             
            
                  and a.yimiao_new_money >= #{params.yimiaoNewMoneyStart}   
            
            
                  and a.yimiao_new_money <= #{params.yimiaoNewMoneyEnd}   
            
            
                and a.yimiao_renqun like CONCAT('%',#{params.yimiaoRenqun},'%')
            
            
                and a.yimiao_text like CONCAT('%',#{params.yimiaoText},'%')
            
            
                and a.yimiao_content like CONCAT('%',#{params.yimiaoContent},'%')
            
            
                  and a.yimiao_delete >= #{params.yimiaoDeleteStart}   
            
            
                  and a.yimiao_delete <= #{params.yimiaoDeleteEnd}   
            
             
                and a.yimiao_delete = #{params.yimiaoDelete}
             

        

        order by a.${params.orderBy} desc
